package SDET;
/*
 * If the n is divisible by 2 then return true or else false
 */
public class PowerOfTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n= 16;
		
		if (n/2==0) {
			System.out.println("Power of 2"+" "+"True");
		}
		else if (n==1) {
			System.out.println("Power of 2"+" "+"True");
			
		}
		else {
			System.out.println("Not the power of 2"+" " +"False");
		}

	}

}
